import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SuccessPage() {
  const navigate = useNavigate();
  const [countdown, setCountdown] = useState(4);

  useEffect(() => {
    const interval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    const timer = setTimeout(() => {
      navigate("/pay");
    }, 46555000);
    return () => {
      clearInterval(interval);
      clearTimeout(timer);
    };
  }, [navigate]);

  return (
    <div style={styles.pageContainer} className="page-container">
      {/* Responsive Styles */}
      <style>
        {`
        /* Reset placeholder styles if any input needed */
        /* Responsive media queries for layout and font sizes */

        /* Extra small devices (300px - 576px) */
        @media (min-width: 300px) and (max-width: 576px) {
          .card-wrapper {
            padding: 20px 15px !important;
            max-width: 90% !important;
          }
          .card-wrapper h2 {
            font-size: 1.5rem !important;
          }
          .card-wrapper p {
            font-size: 0.9rem !important;
          }
          .checkmark-svg {
            width: 80px !important;
            height: 80px !important;
          }
          .redirect-btn {
            width: 100% !important;
            font-size: 0.9rem !important;
            padding: 10px !important;
          }
        }

        /* Tablets (577px - 991px) */
        @media (min-width: 577px) and (max-width: 991px) {
          .card-wrapper {
            max-width: 400px !important;
          }
          .checkmark-svg {
            width: 100px !important;
            height: 100px !important;
          }
        }

        /* Laptop (992px - 1440px) */
        @media (min-width: 992px) and (max-width: 1440px) {
          .card-wrapper {
            max-width: 500px !important;
          }
          .checkmark-svg {
            width: 120px !important;
            height: 120px !important;
          }
        }

        /* Large screens (1441px and above) */
        @media (min-width: 1441px) {
          .card-wrapper {
            max-width: 600px !important;
          }
          .checkmark-svg {
            width: 150px !important;
            height: 150px !important;
          }
        }
        `}
      </style>

      <div style={styles.pageContainer}>
        <SuccessCard countdown={countdown} />
      </div>
    </div>
  );
}

// Styles
const styles = {
  pageContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    backgroundImage: "url('/assets/images/succesban.png')",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundAttachment: "fixed",
    backgroundPosition: "center",
    fontFamily: "'Segoe UI', sans-serif",
    padding: "20px",
  },
  // Main card wrapper styles (responsive via media queries)
  // No need to inline styles for inner components here
};

// SuccessCard component
function SuccessCard({ countdown }) {
  return (
    <div
      className="card-wrapper animate__animated animate__fadeInDown"
      style={{
        borderRadius: "20px",
        background: "rgba(255, 255, 255, 0.9)",
        backdropFilter: "blur(12px)",
        padding: "40px 30px",
        width: "100%",
        maxWidth: "600px",
        boxShadow: "0 8px 16px rgba(0,0,0,0.2)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
      }}
    >
      <HeaderSection />
      <ThankYouMessage />
      <CheckmarkAnimation />
      <CountdownMessage countdown={countdown} />
      <AdditionalInfo />
      <Footer />
    </div>
  );
}

// Components with scaled styles
function HeaderSection() {
  return (
    <h2
      className="text-success mb-3"
      style={{
        fontSize: "2rem",
        marginBottom: "15px",
        fontWeight: "bold",
      }}
    >
      ðŸŽ‰ Payment Successful!
    </h2>
  );
}

function ThankYouMessage() {
  return (
    <p
      className="text-muted"
      style={{
        fontSize: "1.1rem",
        lineHeight: "1.5",
        marginBottom: "25px",
        maxWidth: "400px",
        textAlign: "center",
      }}
    >
      Thank you for your payment. Your transaction has been securely processed.
    </p>
  );
}

function CheckmarkAnimation() {
  return (
    <div style={{ margin: "20px 0" }} className="my-4">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="120"
        height="120"
        fill="none"
        stroke="green"
        strokeWidth="4"
        viewBox="0 0 24 24"
        className="mx-auto checkmark-svg animate__animated animate__bounceIn"
      >
        <path d="M5 13l4 4L19 7" />
      </svg>
    </div>
  );
}

function CountdownMessage({ countdown }) {
  return (
    <p
      style={{
        fontSize: "1rem",
        marginTop: "10px",
        padding: "0 10px",
        color: "#555",
      }}
      className="text-secondary small mt-3"
    >
      Redirecting back to payment page in{" "}
      <strong style={{ color: "#28a745" }}>{countdown}</strong> seconds...
    </p>
  );
}

function AdditionalInfo() {
  return (
    <div
      style={{
        marginTop: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <p
        style={{
          marginBottom: "10px",
          fontSize: "0.9rem",
          color: "#555",
          textAlign: "center",
          maxWidth: "350px",
        }}
        className="text-muted small mb-2"
      >
        If you are not redirected automatically, please click the button below.
      </p>
      <button
        style={{
          padding: "10px 20px",
          fontSize: "1rem",
          borderRadius: "8px",
          border: "none",
          backgroundColor: "#28a745",
          color: "#fff",
          cursor: "pointer",
          boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
          transition: "all 0.3s ease",
        }}
        className="redirect-btn"
        onClick={() => (window.location.href = "/pay")}
      >
        Go to Payment Page
      </button>
    </div>
  );
}

function Footer() {
  return (
    <div
      style={{
        marginTop: "20px",
        fontSize: "0.9rem",
        color: "#555",
        textAlign: "center",
      }}
    >
      <p>&copy; 2024 YourCompany. All rights reserved.</p>
    </div>
  );
}
