import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// ✅ Razorpay script loader
const loadRazorpayScript = () => {
  return new Promise((resolve) => {
    if (window.Razorpay) return resolve(true);
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => resolve(true);
    script.onerror = () => resolve(false);
    document.body.appendChild(script);
  });
};

export default function PaymentPage() {
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const showMessage = (text, type = "info") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  const handlePayment = async () => {
    if (!amount || Number(amount) <= 0) {
      showMessage("⚠️ Please enter a valid amount", "warning");
      return;
    }

    setLoading(true);
    try {
      const { data } = await axios.post(
        "https://payment-secure.onrender.com/api/create-order",
        { amount }
      );

      const { id: order_id, amount: order_amount, currency, razorpayKey } = data;

      const scriptLoaded = await loadRazorpayScript();
      if (!scriptLoaded) {
        showMessage("❌ Failed to load Razorpay SDK", "danger");
        return;
      }

      const options = {
        key: razorpayKey,
        amount: order_amount,
        currency,
        name: "Freelance Services",
        description: "Secure Payment for Web Development",
        order_id,
        handler: async (response) => {
          try {
            const verify = await axios.post(
              "https://payment-secure.onrender.com/api/verify-payment",
              {
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
              }
            );

            if (verify.data.ok) {
              navigate("/success");
            } else {
              showMessage("⚠️ Payment verification failed.", "danger");
            }
          } catch (err) {
            console.error(err);
            showMessage("❌ Error verifying payment.", "danger");
          }
        },
        prefill: {
          name: "Client",
          email: "client@example.com",
          contact: "9876543210",
        },
        theme: { color: "#0d6efd" },
      };

      const rzp = new window.Razorpay(options);
      rzp.on("payment.failed", (res) => {
        console.error("Payment failed", res.error);
        showMessage("❌ Payment failed: " + res.error.description, "danger");
      });
      rzp.open();
    } catch (err) {
      console.error(err);
      showMessage("❌ Error creating order.", "danger");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      {/* ✅ Animated Bootstrap Alert */}
      {message && (
        <div
          className={`alert alert-${message.type} text-center slide-alert`}
          role="alert"
        >
          {message.text}
        </div>
      )}

      {/* Fixed Bottom Card */}
      <div className="bottom-card">
        <div className="bottom-card-header">
          💳 Make a Payment
        </div>
        <p className="bottom-card-text">
          Professional Web Development • Secure Online Payment
        </p>

        <div className="payment-input-group">
          <input
            type="number"
            placeholder="Enter amount (₹)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="custom-input"
          />
          <button
            onClick={handlePayment}
            disabled={loading}
            className="pay-btn"
          >
            {loading ? "Processing..." : "Pay Now"}
          </button>
        </div>
        <div className="security-note">
          🔒 Transactions secured by Razorpay
        </div>
      </div>

      {/* External CSS styles in a style tag or CSS file */}
      <style jsx>{`
        /* Container with background banner */
        .payment-container {
          height: 100vh;
          width: 100vw;
          overflow: hidden;
          background: #030638
            url('/assets/images/backbanner.png') no-repeat center center fixed;
          background-size: cover;
          font-family: 'Segoe UI', sans-serif;
          display: flex;
          flex-direction: column;
          justify-content: flex-end;
          color: white;
          position: relative;
        }

        /* Alert slide animation */
        .slide-alert {
          position: fixed;
          top: 20px;
          left: 20px;
          right: 20px;
          max-width: 500px;
          margin: auto;
          z-index: 1050;
          animation: slideInOut 4s ease forwards;
        }

        @keyframes slideInOut {
          0% {
            opacity: 0;
            transform: translateX(-100%);
          }
          10% {
            opacity: 1;
            transform: translateX(0);
          }
          80% {
            opacity: 1;
            transform: translateX(0);
          }
          100% {
            opacity: 0;
            transform: translateX(100%);
          }
        }

        /* Fixed bottom card */
        .bottom-card {
          position: fixed;
          bottom: 0;
          left: 0;
          right: 0;
          border-top-left-radius: 20px;
          border-top-right-radius: 20px;
          box-shadow: 0 -6px 20px rgba(0, 0, 0, 0.5);
          padding: 25px 20px;
          background: rgba(255, 255, 255, 0.08);
          backdrop-filter: blur(10px);
          text-align: center;
        }

        .bottom-card-header {
          margin-bottom: 10px;
          font-weight: bold;
          font-size: 1.4rem;
          color: #00d9ff;
        }

        .bottom-card-text {
          font-size: 1rem;
          color: #dcdcdc;
        }

        /* Input group styling */
        .payment-input-group {
          display: flex;
          justify-content: center;
          align-items: center;
          margin-top: 15px;
        }

        /* Custom input styles */
        .custom-input {
          border-radius: 10px;
          padding: 12px;
          border: 2px solid #0d6efd;
          background: #1a1a3d;
          color: white;
          margin-right: 10px;
          flex: 1;
          max-width: 280px;
        }

        /* Remove number input spin buttons */
        .custom-input[type='number']::-webkit-inner-spin-button,
        .custom-input[type='number']::-webkit-outer-spin-button {
          -webkit-appearance: none;
          margin: 0;
        }

        .custom-input[type='number'] {
          -moz-appearance: textfield;
        }

        /* Pay Button */
        .pay-btn {
          background: linear-gradient(90deg, #0d6efd, #6f42c1);
          border: none;
          border-radius: 10px;
          padding: 12px 24px;
          min-width: 140px;
          font-weight: bold;
          color: white;
          cursor: pointer;
          transition: all 0.3s ease;
        }
        .pay-btn:hover {
          opacity: 0.9;
        }
        .pay-btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        /* Security note */
        .security-note {
          margin-top: 15px;
          font-size: 0.9rem;
          color: #aaa;
        }

        /* Responsive styles using media queries */
        @media (min-width: 300px) and (max-width: 576px) {
          .payment-input-group {
            flex-direction: column !important;
          }
          .custom-input {
            margin-bottom: 10px !important;
            margin-right: 0 !important;
            width: 100% !important;
            max-width: 100% !important;
          }
          .pay-btn {
            width: 100% !important;
          }
        }

        @media (min-width: 577px) and (max-width: 991px) {
          .payment-input-group {
            flex-direction: row !important;
          }
          .custom-input {
            max-width: 250px !important;
          }
          .pay-btn {
            min-width: 120px !important;
          }
        }

        @media (min-width: 992px) and (max-width: 1440px) {
          .payment-input-group {
            flex-direction: row !important;
          }
          .custom-input {
            max-width: 280px !important;
          }
          .pay-btn {
            min-width: 140px !important;
          }
        }
      `}</style>
    </div>
  );
}
