import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// Load Razorpay script
const loadRazorpayScript = () => {
  return new Promise((resolve) => {
    if (window.Razorpay) return resolve(true);
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => resolve(true);
    script.onerror = () => resolve(false);
    document.body.appendChild(script);
  });
};

export default function PaymentPage() {
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const showMessage = (text, type = "info") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  const handlePayment = async () => {
    if (!amount || Number(amount) <= 0) {
      showMessage("‚ö†Ô∏è Please enter a valid amount", "warning");
      return;
    }
    setLoading(true);
    try {
      const { data } = await axios.post(
        "https://payment-secure.onrender.com/api/create-order",
        { amount }
      );

      const { id: order_id, amount: order_amount, currency, razorpayKey } = data;

      const scriptLoaded = await loadRazorpayScript();
      if (!scriptLoaded) {
        showMessage("‚ùå Failed to load Razorpay SDK", "danger");
        return;
      }

      const options = {
        key: razorpayKey,
        amount: order_amount,
        currency,
        name: "Freelance Services",
        description: "Secure Payment for Web Development",
        order_id,
        handler: async (response) => {
          try {
            const verify = await axios.post(
              "https://payment-secure.onrender.com/api/verify-payment",
              {
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
              }
            );
            if (verify.data.ok) {
              navigate("/success");
            } else {
              showMessage("‚ö†Ô∏è Payment verification failed.", "danger");
            }
          } catch (err) {
            console.error(err);
            showMessage("‚ùå Error verifying payment.", "danger");
          }
        },
        prefill: {
          name: "Client",
          email: "client@example.com",
          contact: "9876543210",
        },
        theme: { color: "#0d6efd" },
      };

      const rzp = new window.Razorpay(options);
      rzp.on("payment.failed", (res) => {
        console.error("Payment failed", res.error);
        showMessage("‚ùå Payment failed: " + res.error.description, "danger");
      });
      rzp.open();
    } catch (err) {
      console.error(err);
      showMessage("‚ùå Error creating order.", "danger");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-background">
      {/* Styles for responsiveness */}
      <style>
        {`
          /* Reset placeholder styling */
          .custom-input::placeholder {
            color: white;
            opacity: 0.8;
          }
          /* Remove number input arrows */
          .custom-input::-webkit-outer-spin-button,
          .custom-input::-webkit-inner-spin-button {
            -webkit-appearance: none;
            margin: 0;
          }
          .custom-input[type=number] {
            -moz-appearance: textfield;
          }
          /* Focus style */
          .custom-input:focus {
            outline: none;
            border-color: #00d9ff !important;
            box-shadow: 0 0 8px #00d9ff;
          }

          /* Alert animation */
          .slide-alert {
            position: fixed;
            top: 20px;
            left: 20px;
            right: 20px;
            max-width: 500px;
            margin: auto;
            z-index: 1050;
            animation: slideInOut 4s ease forwards;
          }

          @keyframes slideInOut {
            0% { opacity: 0; transform: translateX(-100%); }
            10% { opacity: 1; transform: translateX(0); }
            80% { opacity: 1; transform: translateX(0); }
            100% { opacity: 0; transform: translateX(100%); }
          }

          /* Background styling */
          .page-background {
            min-height: 100vh;
            width: 100%;
            background: #030638 url('/assets/images/backbanner.png') no-repeat center center fixed;
            background-size: cover;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
            font-family: "'Segoe UI', sans-serif";
            overflow: hidden;
            padding: 0 10px; /* slight padding for small screens */
          }

          /* ------------------- Responsive media queries ------------------- */

          /* Up to 250px - very small screens */
          @media (max-width: 250px) {
            .page-background {
              background-position: top;
              background-size: contain;
            }
          }

          /* 251px - 576px (small mobile) */
          @media (min-width: 251px) and (max-width: 576px) {
            .page-background {
              background-position: top;
              background-size: contain;
            }
          }

          /* 577px - 768px (small tablet) */
          @media (min-width: 577px) and (max-width: 768px) {
            .page-background {
              background-position: center;
              background-size: cover;
            }
          }

          /* 769px - 991px (large tablet / small desktop) */
          @media (min-width: 769px) and (max-width: 991px) {
            .page-background {
              background-position: center;
              background-size: cover;
            }
          }

          /* 992px - 1199px (small desktop) */
          @media (min-width: 992px) and (max-width: 1199px) {
            .page-background {
              background-position: center;
              background-size: cover;
            }
          }

          /* 1200px - 1440px (desktop) */
          @media (min-width: 1200px) and (max-width: 1440px) {
            .page-background {
              background-position: center;
              background-size: cover;
            }
          }

          /* 1441px - 2000px (large desktop) */
          @media (min-width: 1441px) and (max-width: 2000px) {
            .page-background {
              background-position: center;
              background-size: cover;
            }
          }
        `}
      </style>

      {/* Alert message */}
      {message && (
        <div
          className={`alert alert-${message.type} text-center slide-alert`}
          role="alert"
        >
          {message.text}
        </div>
      )}

      {/* Content container */}
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "flex-end",
          minHeight: "100vh",
        }}
      >
        {/* Fixed Bottom Card */}
        <div
          style={{
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            borderTopLeftRadius: "20px",
            borderTopRightRadius: "20px",
            boxShadow: "0 -6px 20px rgba(0,0,0,0.5)",
            padding: "25px 20px",
            background: "rgba(255, 255, 255, 0.08)",
            backdropFilter: "blur(10px)",
            textAlign: "center",
            width: "100%",
            maxWidth: "100%",
          }}
        >
          <div
            style={{
              marginBottom: "10px",
              fontWeight: "bold",
              fontSize: "1.4rem",
              color: "#00d9ff",
            }}
          >
            üí≥ Make a Payment
          </div>
          <p style={{ fontSize: "1rem", color: "#dcdcdc" }}>
            Professional Web Development ‚Ä¢ Secure Online Payment
          </p>

          {/* Payment Input Group */}
          <div
            className="payment-input-group"
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              marginTop: "15px",
              flexWrap: "wrap",
            }}
          >
            <input
              type="number"
              placeholder="Enter amount (‚Çπ)"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="custom-input form-control form-control-lg"
              style={{
                borderRadius: "10px",
                padding: "12px",
                border: "2px solid #0d6efd",
                background: "#1a1a3d",
                color: "white",
                marginRight: "10px",
                flex: "1 1 auto",
                minWidth: "120px",
                maxWidth: "280px",
              }}
            />
            <button
              onClick={handlePayment}
              disabled={loading}
              className="pay-btn"
              style={{
                background: "linear-gradient(90deg, #0d6efd, #6f42c1)",
                border: "none",
                borderRadius: "10px",
                padding: "12px 24px",
                minWidth: "140px",
                fontWeight: "bold",
                color: "white",
                cursor: "pointer",
                transition: "all 0.3s ease",
                marginTop: "10px",
              }}
              onMouseOver={(e) => (e.currentTarget.style.opacity = "0.9")}
              onMouseOut={(e) => (e.currentTarget.style.opacity = "1")}
            >
              {loading ? "Processing..." : "Pay Now"}
            </button>
          </div>
          <div style={{ marginTop: "15px", fontSize: "0.9rem", color: "#aaa" }}>
            üîí Transactions secured by Razorpay
          </div>
        </div>
      </div>
    </div>
  );
}
