import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// ✅ Razorpay script loader
const loadRazorpayScript = () => {
  return new Promise((resolve) => {
    if (window.Razorpay) return resolve(true);
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => resolve(true);
    script.onerror = () => resolve(false);
    document.body.appendChild(script);
  });
};

export default function PaymentPage() {
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const showMessage = (text, type = "info") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  const handlePayment = async () => {
    if (!amount || Number(amount) <= 0) {
      showMessage("⚠️ Please enter a valid amount", "warning");
      return;
    }

    setLoading(true);
    try {
      const { data } = await axios.post(
        "https://payment-secure.onrender.com/api/create-order",
        { amount }
      );

      const { id: order_id, amount: order_amount, currency, razorpayKey } = data;

      const scriptLoaded = await loadRazorpayScript();
      if (!scriptLoaded) {
        showMessage("❌ Failed to load Razorpay SDK", "danger");
        return;
      }

      const options = {
        key: razorpayKey,
        amount: order_amount,
        currency,
        name: "Freelance Services",
        description: "Secure Payment for Web Development",
        order_id,
        handler: async (response) => {
          try {
            const verify = await axios.post(
              "https://payment-secure.onrender.com/api/verify-payment",
              {
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
              }
            );

            if (verify.data.ok) {
              navigate("/success");
            } else {
              showMessage("⚠️ Payment verification failed.", "danger");
            }
          } catch (err) {
            console.error(err);
            showMessage("❌ Error verifying payment.", "danger");
          }
        },
        prefill: {
          name: "Client",
          email: "client@example.com",
          contact: "9876543210",
        },
        theme: { color: "#0d6efd" },
      };

      const rzp = new window.Razorpay(options);
      rzp.on("payment.failed", (res) => {
        console.error("Payment failed", res.error);
        showMessage("❌ Payment failed: " + res.error.description, "danger");
      });
      rzp.open();
    } catch (err) {
      console.error(err);
      showMessage("❌ Error creating order.", "danger");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      {message && (
        <div className={`alert alert-${message.type} text-center slide-alert`} role="alert">
          {message.text}
        </div>
      )}

      <div className="bottom-card">
        <div className="bottom-card-header">💳 Make a Payment</div>
        <p className="bottom-card-text">
          Professional Web Development • Secure Online Payment
        </p>

        <div className="payment-input-group">
          <input
            type="number"
            placeholder="Enter amount (₹)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="custom-input"
          />
          <button onClick={handlePayment} disabled={loading} className="pay-btn">
            {loading ? "Processing..." : "Pay Now"}
          </button>
        </div>

        <div className="security-note">
          🔒 Transactions secured by Razorpay
        </div>
      </div>
    </div>
  );
}
