import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SuccessPage() {
  const navigate = useNavigate();
  const [countdown, setCountdown] = useState(4);

  useEffect(() => {
    const interval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    const timer = setTimeout(() => {
      navigate("/pay");
    }, 45555000);
    return () => {
      clearInterval(interval);
      clearTimeout(timer);
    };
  }, [navigate]);

  return (
    <div style={styles.pageContainer}>
      {/* Embedded CSS for full responsiveness and detailed media queries */}
      <style>
        {`
        /* Reset & base styles for responsiveness */

        /* -- Container adjustments for smaller screens */
        @media (max-width: 576px) {
          .card {
            max-width: 90% !important;
            padding: 20px !important;
            border-radius: 15px !important;
          }
          .header {
            font-size: 1.5rem !important;
          }
          .thank-you {
            font-size: 1rem !important;
            line-height: 1.4 !important;
          }
          .checkmark-svg {
            width: 80px !important;
            height: 80px !important;
          }
          .countdown {
            font-size: 0.9rem !important;
          }
          .button {
            width: 100% !important;
            font-size: 0.9rem !important;
            padding: 10px !important;
          }
          .footer-text {
            font-size: 0.8rem !important;
          }
        }

        @media (min-width: 577px) and (max-width: 767px) {
          .card {
            max-width: 85% !important;
            padding: 25px !important;
            border-radius: 17px !important;
          }
          .header {
            font-size: 2rem !important;
          }
          .thank-you {
            font-size: 1.1rem !important;
          }
          .checkmark-svg {
            width: 100px !important;
            height: 100px !important;
          }
          .countdown {
            font-size: 1rem !important;
          }
          .button {
            min-width: 180px !important;
          }
          .footer-text {
            font-size: 0.85rem !important;
          }
        }

        @media (min-width: 768px) and (max-width: 991px) {
          .card {
            max-width: 70% !important;
            padding: 30px !important;
            border-radius: 20px !important;
          }
          .header {
            font-size: 2.5rem !important;
          }
          .thank-you {
            font-size: 1.2rem !important;
          }
          .checkmark-svg {
            width: 120px !important;
            height: 120px !important;
          }
          .countdown {
            font-size: 1.1rem !important;
          }
          .button {
            min-width: 200px !important;
          }
          .footer-text {
            font-size: 0.9rem !important;
          }
        }

        @media (min-width: 992px) and (max-width: 1199px) {
          .card {
            max-width: 60% !important;
            padding: 35px !important;
            border-radius: 22px !important;
          }
          .header {
            font-size: 3rem !important;
          }
          .thank-you {
            font-size: 1.3rem !important;
          }
          .checkmark-svg {
            width: 150px !important;
            height: 150px !important;
          }
          .countdown {
            font-size: 1.2rem !important;
          }
          .button {
            min-width: 220px !important;
          }
          .footer-text {
            font-size: 1rem !important;
          }
        }

        @media (min-width: 1200px) {
          /* Large screens / desktops */
          .card {
            max-width: 50% !important;
            padding: 40px !important;
            border-radius: 30px !important;
          }
          .header {
            font-size: 3.5rem !important;
          }
          .thank-you {
            font-size: 1.4rem !important;
          }
          .checkmark-svg {
            width: 180px !important;
            height: 180px !important;
          }
          .countdown {
            font-size: 1.3rem !important;
          }
          .button {
            min-width: 250px !important;
          }
          .footer-text {
            font-size: 1.1rem !important;
          }
        }

        /* Additional fine-tuning for extremely large screens */
        @media (min-width: 1800px) {
          .card {
            max-width: 40% !important;
            padding: 50px !important;
            border-radius: 35px !important;
          }
          .header {
            font-size: 4rem !important;
          }
          .thank-you {
            font-size: 1.6rem !important;
          }
          .checkmark-svg {
            width: 200px !important;
            height: 200px !important;
          }
          .countdown {
            font-size: 1.5rem !important;
          }
          .button {
            min-width: 300px !important;
          }
          .footer-text {
            font-size: 1.2rem !important;
          }
        }

        /* General styles for elements */
        .card {
          transition: all 0.3s ease;
        }
        `}
      </style>

      {/* Outer container styling */}
      <div style={styles.innerContainer}>
        <SuccessCard countdown={countdown} />
      </div>
    </div>
  );
}

// Styles for outer container
const styles = {
  pageContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    backgroundImage: "url('/assets/images/succesban.png')",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundAttachment: "fixed",
    backgroundPosition: "center",
    fontFamily: "'Segoe UI', sans-serif",
    padding: "20px",
  },
  innerContainer: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
};

// Main Card component with responsiveness
function SuccessCard({ countdown }) {
  return (
    <div
      className="card"
      style={{
        borderRadius: "20px",
        background: "rgba(255, 255, 255, 0.9)",
        backdropFilter: "blur(12px)",
        boxShadow: "0 8px 16px rgba(0,0,0,0.2)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 30px",
        width: "100%",
        maxWidth: "600px",
        boxSizing: "border-box",
        transition: "all 0.3s ease",
      }}
    >
      <HeaderSection />
      <ThankYouMessage />
      <CheckmarkAnimation />
      <CountdownMessage countdown={countdown} />
      <AdditionalInfo />
      <Footer />
    </div>
  );
}

// Components with responsive inline styles

function HeaderSection() {
  return (
    <h2
      className="header"
      style={{
        fontSize: "clamp(1.5rem, 5vw, 2.5rem)",
        marginBottom: "15px",
        fontWeight: "bold",
        textAlign: "center",
        margin: "0 10px",
      }}
    >
      ðŸŽ‰ Payment Successful!
    </h2>
  );
}

function ThankYouMessage() {
  return (
    <p
      className="thank-you"
      style={{
        fontSize: "clamp(1rem, 4vw, 1.2rem)",
        lineHeight: 1.5,
        marginBottom: "25px",
        maxWidth: "400px",
        textAlign: "center",
      }}
    >
      Thank you for your payment. Your transaction has been securely processed.
    </p>
  );
}

function CheckmarkAnimation() {
  return (
    <div style={{ margin: "20px 0" }}>
      <svg
        xmlns="http://www.w3.org/2000/svg"
        className="checkmark-svg"
        viewBox="0 0 24 24"
        fill="none"
        stroke="green"
        strokeWidth="4"
        style={{
          width: "clamp(80px, 10vw, 150px)",
          height: "clamp(80px, 10vw, 150px)",
        }}
      >
        <path d="M5 13l4 4L19 7" />
      </svg>
    </div>
  );
}

function CountdownMessage({ countdown }) {
  return (
    <p
      className="countdown"
      style={{
        fontSize: "clamp(0.9rem, 3vw, 1.1rem)",
        marginTop: "10px",
        padding: "0 10px",
        color: "#555",
        textAlign: "center",
      }}
    >
      Redirecting back to payment page in{" "}
      <strong style={{ color: "#28a745" }}>{countdown}</strong> seconds...
    </p>
  );
}

function AdditionalInfo() {
  return (
    <div
      style={{
        marginTop: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "0 10px",
        width: "100%",
      }}
    >
      <p
        style={{
          marginBottom: "10px",
          fontSize: "0.9rem",
          color: "#555",
          textAlign: "center",
          maxWidth: "350px",
        }}
        className="text-muted small mb-2"
      >
        If you are not redirected automatically, please click the button below.
      </p>
      <button
        className="redirect-btn"
        style={{
          padding: "10px 20px",
          fontSize: "clamp(1rem, 3vw, 1.2rem)",
          borderRadius: "8px",
          border: "none",
          backgroundColor: "#28a745",
          color: "#fff",
          cursor: "pointer",
          boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
          transition: "all 0.3s ease",
          width: "auto",
          minWidth: "150px",
        }}
        onClick={() => (window.location.href = "/pay")}
      >
        Go to Payment Page
      </button>
    </div>
  );
}

function Footer() {
  return (
    <div
      style={{
        marginTop: "20px",
        fontSize: "clamp(0.8rem, 2.5vw, 1rem)",
        color: "#555",
        textAlign: "center",
      }}
    >
      <p>&copy; 2024 YourCompany. All rights reserved.</p>
    </div>
  );
}
